import React, { Component } from 'react';
import AppNav from './AppNav';
import DatePicker from 'react-datepicker';
import "./App.css";
import "react-datepicker/dist/react-datepicker.css";
import { Container, Form, FormGroup, Button, Label, Input } from 'reactstrap';
import { Link } from 'react-router-dom'; 

class Expenses extends Component {
    state = {
        date: new Date(),
        isLoading: true,
        expenses: []
      }

      async componentDidMount(){
        const response= await fetch('/api/1.0/categories');
        const body = await response.json();

        this.setState({expenses : body, isLoading: false});

      }

    handleChange


    render() { 

        const title= <h3>Add Expense</h3>;
        const {expenses, isLoading }= this.state;

        return ( 
            <div>
                <AppNav/> 
                 
                <Container>
                    {title}
                    <Form onSubmit={this.handleSubmit}>
                        <FormGroup>
                            <Label for="title">Title</Label>
                            <Input type="text" name="title" id="title" onChange={this.handleChange} autoComplete="name"/>
                        </FormGroup>

                        <FormGroup>
                            <Label for="category">Category</Label>
                            <Input type="text" name="category" id="category" onChange={this.handleChange}/>
                        </FormGroup>

                        <FormGroup>
                            <Label for="date"> Date</Label>
                            <DatePicker selected={this.state.date}  onChange={this.handleDateChange}/>
                        </FormGroup>

                        <div className="row">
                            <FormGroup className="col-md-4 mb-3" >
                                <Label for="location">Location</Label>
                                <Input type="text" name="location" id="location" />
                            </FormGroup>
                        </div>
                        

                        <FormGroup>
                            <Button color="primary" type="submit" >Save</Button>{' '}
                            <Button color="secondary" tag={Link} to="/" >Cancel</Button>
                        </FormGroup>

                    </Form>
                </Container>
            </div>     
        );
    }
}
 
export default Expenses;
