import React, { Component } from 'react';
import AppNav from './AppNav';
import { fetchCategoryData } from './api/fetchData';


class Category extends Component {
    state = { 
        isLoading: true,
        Categories : []
     }

     async componentDidMount(){
         const response = await fetchCategoryData();
         console.log(response.data);
         const body = await response.data;
         this.setState({Categories : body, isLoading : false});

     }

    render() { 
        const { Categories, isLoading } = this.state;
        if(isLoading)
            return (<div>Loading ..... </div>);

        return (  
            
            <div>
             <AppNav/>
                <h2>Categories</h2>
                {
                    Categories.map(category =>
                            <div id={category.categoryId} key={category.categoryId} >
                                { category.categoryName}    
                            </div>
                    )

                }
                <Container>
                    {title}
                    <Form onSubmit={this.handleSubmit}>
                        <FormGroup>
                            <Label for="category">Category</Label>
                            <Input type="text" name="description" id="title" onChange={this.handleChange} autoComplete="name"/>
                        </FormGroup>

                        <FormGroup>
                            <Label for="category">Category</Label>
                            <select>
                                {categoryList}
                            </select>
                            
                        </FormGroup>

                        <FormGroup>
                            <Label for="date"> Date</Label>
                            <DatePicker selected={this.state.item.expenseDate}  onChange={this.handleDateChange}/>
                        </FormGroup>

                        <div className="row">
                            <FormGroup className="col-md-4 mb-3" >
                                <Label for="location">Location</Label>
                                <Input type="text" name="location" id="location" />
                            </FormGroup>
                        </div>
                        

                        <FormGroup>
                            <Button color="primary" type="submit" >Save</Button>{' '}
                            <Button color="secondary" tag={Link} to="/" >Cancel</Button>
                        </FormGroup>

                    </Form>
                </Container>


                <Container>
                    <h3> Category List </h3>
                    <Table className="mt-4">
                        <thead>
                            <tr>
                                <th width="40%">Description</th>
                                <th width="15%">Amount</th>
                                <th width="25%">Date</th>
                                <th>Category</th>
                                <th width="10%">Action</th>
                            </tr> 
                        </thead>
                        <tbody>
                            {rows}
                        </tbody>

                    </Table>
                </Container>
            </div>
        );
    }
}
 
export default Category;
