import React, { Component } from 'react';
import AppNav from './AppNav';
import { fetchCategoryData } from './api/fetchData';
import { Table, Container, Form, FormGroup, Button, Label, Input } from 'reactstrap';
import { Link } from 'react-router-dom';


class Category extends Component {
    state = { 
        isLoading: true,
        Categories : []
     }

     async componentDidMount(){
         const response = await fetchCategoryData();
         console.log(response.data);
         const body = await response.data;
         this.setState({Categories : body, isLoading : false});

     }



    render() { 
        const { Categories, isLoading } = this.state;
        if(isLoading)
            return (<div>Loading ..... </div>);

        let rows=
        expenses.map(expense =>
            <tr key={expense.id}>
                <td>{expense.description}</td>
                <td>{expense.amount}</td>
                <td>{expense.expenseDate}</td>
                <td>{expense.category.categoryName}</td>
                <td><Button size="sm" color="danger" onClick={()=> this.remove(expense.id)}>Delete</Button></td>
            </tr>
        
        )    

        return (  
            
            <div>
             <AppNav/>
                <h2>Categories</h2>
                {
                    Categories.map(category =>
                            <div id={category.categoryId} key={category.categoryId} >
                                { category.categoryName}    
                            </div>
                    )

                }
                <Container>
                    {title}
                    <Form onSubmit={this.handleSubmit}>
                        <FormGroup>
                            <Label for="category">Category</Label>
                            <Input type="text" name="description" id="title" onChange={this.handleChange} autoComplete="name"/>
                        </FormGroup>

                        <FormGroup>
                            <Button color="primary" type="submit" >Save</Button>{' '}
                            <Button color="secondary" tag={Link} to="/" >Cancel</Button>
                        </FormGroup>

                    </Form>
                </Container>

                <Container>
                    <h3> Category List </h3>
                    <Table className="mt-4">
                        <thead>
                            <tr>
                                <th width="40%">Category Name</th>
                                <th width="10%">Action</th>
                            </tr> 
                        </thead>
                        <tbody>
                            {rows}
                        </tbody>

                    </Table>
                </Container>
            </div>
        );
    }
}
 
export default Category;
